package com.example.biapp;

import androidx.test.ext.junit.runners.AndroidJUnit4;

import com.biapp.utils.GsonUtil;
import com.biapp.utils.PrintfUtil;
import com.biapp.utils.TLVUtils;

import org.junit.Test;
import org.junit.runner.RunWith;

import java.util.List;

import aura.data.Bytes;

@RunWith(AndroidJUnit4.class)
public class TLVTest {

    public String PKCS1_PUBLIC_KEY = "";

    @Test
    public void test(String[] args) {
        List<TLVUtils.TLV> tlvs = TLVUtils.parseDER(Bytes.fromHexString(PKCS1_PUBLIC_KEY));
        PrintfUtil.d("tlv", GsonUtil.toJson(tlvs));
    }
}
